#1 Combien y a-t-il de handle dans cette array ?
def count_handle(handles_twitter)
	puts "Il y a #{handles_twitter.count} handles dans l'array."
end


#2 Quel est le handle le plus court de cette liste ?
def min_handle (handles_twitter)
	puts ("le handle le plus court de cette liste est #{handles_twitter.sort_by(&:length)[0]}")
end


#3 Combien y-a-t'il de handle contenant 5 caractères?
def cara_handle(handles_twitter) 
	puts "Le nombre de handle contenant 5 caractères est :"
	puts handles_twitter.count{|a| a.length == 6}
end


#4 Combien commencent par une majuscule (première lettre juste après le @) ?
def uppercase_handle(handles_twitter)
	puts "Le nombre de handle commencant par une majuscule est de :" 
	puts handles_twitter.count {|e| e =~ /^@[A-Z]/}
end


#5 Trie la liste de handle par ordre alphabétique.
 def order_handle(handles_twitter)
 	puts "voici la listes des handles par ordres alphabétique : " 
 	puts handles_twitter.sort_by {|a| a.downcase}
 end


#6 Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)
def size_handle(handles_twitter)
	puts "Liste croissante des handles : "
	puts handles_twitter.sort {|a, b| a.length - b.length}
end


#7 Quelle est la position dans l'array de la personne @epenser? 
def epenser_handle(handles_twitter)
	puts handles_twitter.find_index("@epenser") + 1 
end 


